function [msg] = GetInfo(MCC)% msg = GetInfo(MCC)%% INPUTS%    MCC -- MClust cluster object% % OUTPUTS%    msg -- cell array message%% ADR 1999%% Status: PROMOTED (Release version) % See documentation for copyright (owned by original authors) and warranties (none!).% This code released as part of MClust 3.0.% Version control M3.0.msg = {};iM = 1;f = FindInCluster(MCC);msg{iM} = [num2str(length(f)) ' points']; iM = iM+1;msg{iM} = ''; iM = iM+1;msg{iM} = 'Limits:'; iM = iM+1;nBounds = length(MCC.xdimNames);if nBounds > 0	for iL = 1:nBounds		msg{iM} = ['   ' MCC.xdimNames{iL} ' x ' MCC.ydimNames{iL}]; iM = iM+1;	endelse   msg{iM} = 'none';end%if	